    public javax.swing.JTextArea getErrorArea()
    {
        return errorMessage;
    }
    
    public javax.swing.JLabel getLabel()
    {
        return jLabel1;
    }
    
    public void showError(String message)
    {   
        // on param√®tre la zone de texte
        this.getErrorArea().setText(message);
        this.getErrorArea().setCaretPosition(0);
        this.getErrorArea().setEnabled(true);
        this.getErrorArea().setEditable(false);
        this.getErrorArea().setFocusable(false);
           
        this.setEnabled(true);
        this.setLocationRelativeTo(null);
        this.pack();
        
        this.setVisible(true);
    }
    
